package eu.janvdb.aoc2016.day2;

public class Puzzle {

	private static final String[] INPUT = {
			"",
			"",
			"",
			"",
			""
	};

//	private static final String[] INPUT = {
//			"ULL",
//			"RRDDD",
//			"LURDL",
//			"UUUUD"
//	};

	private static final int A = 10, B = 11, C = 12, D = 13;

	private static final int[][] MOVE_MATRIX = {
			/*       U  R  D  L */
			/* 1 */ {1, 1, 3, 1},
			/* 2 */ {2, 3, 6, 2},
			/* 3 */ {1, 4, 7, 2},
			/* 4 */ {4, 4, 8, 3},
			/* 5 */ {5, 6, 5, 5},
			/* 6 */ {2, 7, A, 5},
			/* 7 */ {3, 8, B, 6},
			/* 8 */ {4, 9, C, 7},
			/* 9 */ {9, 9, 9, 8},
			/* A */ {6, B, A, A},
			/* B */ {7, C, D, A},
			/* C */ {8, C, C, B},
			/* D */ {B, D, D, D}
	};

	public static void main(String[] args) {
		int currentDigit = 5;
		for (String step : INPUT) {
			for (char ch : step.toCharArray()) {
				currentDigit = switch (ch) {
					case 'U' -> MOVE_MATRIX[currentDigit - 1][0];
					case 'R' -> MOVE_MATRIX[currentDigit - 1][1];
					case 'D' -> MOVE_MATRIX[currentDigit - 1][2];
					case 'L' -> MOVE_MATRIX[currentDigit - 1][3];
					default -> throw new IllegalArgumentException("Char " + ch);
				};
			}
			System.out.println(currentDigit);
		}


	}

}
